import {Component} from '@angular/core';
import {NavController} from 'ionic-angular';
import {Platform, ActionSheetController} from 'ionic-angular';

const ninja = 'data:image/jpeg;base64,';

@Component({
  selector: 'page-capture',
  templateUrl: 'capture.html'
})
export class CapturePage {

  public category: string = '';
  public eventList: any[] = [];

  constructor(public navCtrl: NavController,
              public platform: Platform,
              public actionsheetCtrl: ActionSheetController) {

  }

  openMenu() {
    let actionSheet = this.actionsheetCtrl.create({
      title: 'Capture Data',
      cssClass: 'action-sheets-basic-page',
      buttons: [
        {
          text: 'Capture Location',
          icon: !this.platform.is('ios') ? 'pin' : null,
          handler: () => {
            this.captureLocation();
          }
        },
        {
          text: 'Take Picture',
          icon: !this.platform.is('ios') ? 'camera' : null,
          handler: () => {
            this.capturePicture();
          }
        },
        {
          text: 'Scan Barcode',
          icon: !this.platform.is('ios') ? 'barcode' : null,
          handler: () => {
            this.captureBarCode();
          }
        },
        {
          text: 'Clear List',
          role: 'cancel', // will always sort to be on the bottom
          icon: !this.platform.is('ios') ? 'close' : null,
          handler: () => {
            this.clearCache();
          }
        }
      ]
    });

    actionSheet.present();
  }

  captureLocation() {
    this.eventList.push({
      type: 'location',
      title: 'Johnny checked in',
      text: 'Brussels, Belgium',
      image: ninja,
    });
  }

  capturePicture() {
    this.eventList.push({
      type: 'picture',
      title: 'Captured Image',
      text: 'This image was captured at 14:21 PM on the 25\'th of May 2017.',
      image: ninja,
    });
  }

  captureBarCode() {
    this.eventList.push({
      type: 'barcode',
      title: 'Scanned Barcode',
      text: '7826348721394',
    });
  }

  clearCache() {
    this.eventList = [];
  }

}
